cat CNAE_DASH_220521.csv | sed 's/"//g' | awk 'BEGIN{pai="CNAE"; profundidade_velho=0;  FS="#"}{ profundidade=0; for (i=1; i<=NF-1; i++){if ($i!=""){ if (i - profundidade_velho > 1){pai=filho;profundidade_velho=i - 1;}; filho=$i; profundidade=i;}} print "insere_a_direita_dos_filhos(\""pai"\",\""filho"\")"; if ((profundidade - profundidade_velho)>1) {pai=filho; profundidade_velho=profundidade - 1;}; if (profundidade ==0){pai="CNAE"; profundidade_velho=0;}}'
